.chart-widget {
  margin-bottom: 50px;
}

.chart-widget .abp-active {
  color: #737373;
}

.chart-widget .abp-active i {
  background-color: #737373;
}

.chart-widget .abp-data {
  opacity: 0;
  height: 0px;
  overflow: hidden;
  -webkit-transition: opacity 1s ease-in, height 0.2s ease-in;
  -moz-transition: opacity 1s ease-in, height 0.2s ease-in;
  transition: opacity 1s ease-in, height 0.2s ease-in;
}

.chart-widget .abp-data.abp-active {
  height: auto;
  opacity: 1;
}

.chart-widget .abp-data.abp-active,
.chart-widget .abp-no-data {
  border: 1px dashed #ccc;
  padding: 1em;
}

.chart-widget .abp-no-data {
  height: 230px;
  position: relative;
}

.chart-widget .abp-no-data:after {
  content: "No data, select an example!";
  font-weight: bold;
  color: #ccc;
  display: block;
  position: relative;
  top: 50%;
  -webkit-transform: translateY(-50%);
  -moz-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  transform: translateY(-50%);
  text-align: center;
}

css.data_provider_widget.css:
/**
 * Copyright 2014 LaxarJS
 * Released under the MIT license.
 * www.laxarjs.org
 */

.data-provider-widget {
  min-height: 334px;
}

.data-provider-widget .abp-active {
  color: #737373;
}

.data-provider-widget .abp-active i {
  background-color: #737373;
}

.data-provider-widget .abp-col-1 {
  width: 20px;
}

.data-provider-widget .table {
  border-radius: 0;
}

.data-provider-widget tr:first-child > td {
  border-top: none;
}

.data-provider-widget tbody > tr.selected > td {
  color: white;
  font-weight: bold;
  background: #5a97c2;
}

.data-provider-widget .table-hover > tbody > tr:hover > td {
  cursor: pointer;
  background-color: #c6d7ee;
  color: #1f77b4;
  font-weight: bold;
}

